function getPartDay($h){
	if($h>=8 && $h<12) return 'Доброе утро';
	elseif($h>=12 && $h<18) return 'Добрый день';
	elseif($h>=18 && $h<22) return 'Добрый вечер';
	elseif(($h>=22 && $h<24)||($h>=0 && $h<8)) return 'Доброй ночи';
}
